def get_indian_tickers_list():
    # Return top 250 tickers (Maintaining Market Cap Order)
    # Removing duplicates if any but preserving order
    seen = set()
    unique_ordered = []
    for x in INDIAN_TICKERS_RAW:
        if x not in seen:
            unique_ordered.append(x)
            seen.add(x)
            
    return unique_ordered[:250]
